# Author: Nitesh Turaga# Systems Programmer II, Lab - Adrian lee, Magee Womens Research Institute# Description:import mathimport osdef run():    #Open both files    file1 = open("ER.txt","r")    ERE2 = file1.read()    file1.close()        file2 = open("hg18.bed","r")    hg18 = file2.read()    file2.close()    file3 = open("generatedFile.bed","wt")    file3.write(mainFunction(ERE2,hg18))    returndef eliminateSpace(text):    if (text == None):        return None    edited = ""    for i in xrange(len(text)):        if text[i]==text[i-1] and str.isspace(text[i])==True:            continue        edited+=text[i]    return editeddef mainFunction(ER_E2,HG_18):    newStr = ""    newStr+="Chromosome"    newStr+="\t"    newStr+="Start"    newStr+="\t"    newStr+="Stop"    newStr+="\t"    newStr+="Average"    newStr+="    "    newStr+="NM_number"    newStr+="\t"    newStr+="Distance"    newStr+="\n"    for line in ER_E2.splitlines():        ERE2_line_split = line.split("\t")        chrom=ERE2_line_split[0]        average = ERE2_line_split[3]        average_LL = float(average)-50000        average_UL = float(average)+50000        newStr+="\n"        newStr+=ERE2_line_split[0]        newStr+="\t"        newStr+=ERE2_line_split[1]        newStr+="\t"        newStr+=ERE2_line_split[2]        newStr+="\t"        for line2 in HG_18.splitlines():            hg18_split = line2.split("\t")            if (hg18_split[5]=="+" and hg18_split[0]==chrom):                if (float(hg18_split[1])<average_UL and float(hg18_split[1])>average_LL):                    newStr+=hg18_split[3]                    newStr+="\t"                    newStr+=str(float(hg18_split[1])-float(average))                    newStr+="\t"            if (hg18_split[5]=="-" and hg18_split[0]==chrom):                if (float(hg18_split[2])<average_UL and float(hg18_split[2])>average_LL):                    newStr+=hg18_split[3]                    newStr+="\t"                    newStr+=str(float(hg18_split[2])-float(average))                    newStr+="\t"    return newStrrun()